import axios from "axios"
import { useState ,useEffect} from "react";
import '../css/main.css';
import search_icon from '../images/search-copy.png'
import Jokes from "./jokes";
import { useSearch } from "../context/searchProvider";
import { useJoke } from "../context/jokeProvider";
import Joke from "./joke";

const Main = () =>{

  let [search,setSearch] = useState('');
  let {handleSearch,jokes,jokeType,setJoketype} = useSearch();
  let {seeJoke} = useJoke();
  let types = [
    {id : "adult", content : "ADULT JOKES"},
    {id : "dad", content : "DAD JOKES"},
    {id : "christmas", content : "CHRISTMAS JOKES"},
    {id : "job", content : "JOB JOKES"},
    {id : "birthday", content : "BIRTHDAY JOKES"},
    {id : "social", content : "SOCIAL JOKES"},
    {id : "puns", content : "PUNS"},
    {id : "all", content : "VIEW ALL"}
    ];
    // console.log(jokes);

    let handleClick = (type)=>{
        setJoketype(type)
     }
  let options = types.map((joke)=>{
    return(
        <p key = {joke.id} id = {joke.id} onClick = {()=>handleClick(joke.id)}>{joke.content}</p>
    )
  })
  //it executed when the page first loads
  useEffect(()=>{
     handleSearch(jokeType)
  },[])

 //executes if jokeType changes
  useEffect(()=>{
    handleSearch(jokeType)
  },[jokeType])

  let handleInput = (e)=>{
    if(e.target.value === '') return;
    setSearch(e.target.value)
  }
//  let handleSearch = async(search) =>{
//     await axios.get(`https://api.chucknorris.io/jokes/search?query=${search}`)
//     .then((re)=>{
//         setJokes(re.data);
//     })
//  }

    return(
        <main>
            <div className="main">
              <h1>The Joke Bible</h1>
              <p>Daily Laughs for you and yours</p>
              <input name="search" type = 'text' placeholder="How can we make you laugh today?"  onChange={handleInput} />
              <button onClick={()=>handleSearch(search)}>hello</button>
            </div>
            {
              seeJoke ? 
              <Joke/>
              :
              <div className="jokes_div" >
               <div className="joke_options">
                {options}

               </div>
                <p className="joke_ref" >{`${jokeType.toUpperCase()} JOKES`}</p>

              <Jokes jokes = {jokes} />
              </div>
            }



        </main>
    )
}

export default Main;
