import { createContext,useContext ,useState} from "react";

let JokeContext = createContext();

export let useJoke = ()=>{
    return useContext(JokeContext)
}


let JokeProvider = ({children})=>{

    let [seeJoke,setSeeJoke] = useState(false);
    let [joke,setJoke] = useState('');
    let [category,setCategory] = useState('');
    let setseeJoke=()=>{
        setSeeJoke(!seeJoke);
    }
    let changeCategory = (cat) => setCategory(cat);

    let setjoke = (joke) => setJoke(joke);

    return(
         <JokeContext.Provider value = {{joke,seeJoke,setseeJoke,category,changeCategory,setjoke}} >
             {children}
         </JokeContext.Provider>
    )

}

export default JokeProvider;
