import { useJoke } from '../context/jokeProvider';
import '../css/main.css';

export default function Card({category,url,joke}){
   let {changeCategory,setseeJoke,setjoke} = useJoke();

   let handleClick = ()=>{
       changeCategory(category);
       setseeJoke();
       setjoke(joke);
   }
    return(
        <div className="card">
        <h3>{category.length === 0 ? "UNCATEGORIZED" : `${category[0].toUpperCase()} JOKE`}</h3>
        <p>
            {joke}
        </p>
        <a onClick={handleClick}>SEE STATS</a>
       </div>
    )
}
