import axios from "axios"
import { useState ,useEffect} from "react";
import '../css/main.css';
import search_icon from '../images/search-copy.png'

const Main = () =>{

  let [search,setSearch] = useState('');
  let [jokes,setJokes] = useState([]);
  let [jokeType,setJokeType] = useState('adult');
  let types = ['adult','dad','christmas','job','birthday','social','puns']

  let options = types.map((joke)=>{
    return(
        <p key = {joke} id = {joke} onClick = {setJokeType(joke)} >{joke.toUpperCase()} JOKES</p>
    )
  })

  useEffect(()=>{
     handleSearch(jokeType)
  },[])

  let handleInput = (e)=>{
    setSearch(e.target.value)
  }
 let handleSearch = async(search) =>{
    await axios.get(`https://api.chucknorris.io/jokes/search?query=${search}`)
    .then((re)=>{
        setJokes(re.data);
    })
 }

    return(
        <main>
            <div className="main">
              <h1>The Joke Bible</h1>
              <p>Daily Laughs for you and yours</p>
              <input name="search" type = 'text' placeholder="How can we make you laugh today?"  onChange={handleInput} />
              <button onClick={()=>handleSearch(search)}>hello</button>
            </div>

            <div className="jokes" >
            <div className="joke_options">
                {options}
                <p id = "view_all">VIEW ALL</p>
            </div>
            <p className="joke_ref" >{jokeType.toUpperCase()} JOKES</p>

            </div>


        </main>
    )
}

export default Main;
