import { useState } from "react";
import '../css/main.css';
import search_icon from '../images/search-copy.png'

const Main = () =>{

  let [search,setSearch] = useState('');
  let [jokes,setJokes] = useState([]);

  let handleSearch =async()=>{
    setJokes(axios.get(`https://api.chucknorris.io/jokes/search?query=${search}`))
  }

  document.addEventListener('keydown',(e)=>{
    if(search === '') return;
      if(e.key === 'Enter' ){

         handleSearch();
      }
  })


    return(
        <main>
            <div className="main">
              <h1>The Joke Bible</h1>
              <p>Daily Laughs for you and yours</p>
              <input type = 'text' placeholder="How can we make you laugh today?" onChange={(e)=>setSearch(e.target.value)} />

            </div>
        </main>
    )
}

export default Main;
